package condition;

public class ForEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<3; i++) {
			System.out.println("안녕");
		}
		
		for(int j=1; j<=10; j++) {
			System.out.print(j+" ");
		}
		System.out.println();
		for(int j=10;j>0; j--) {
			System.out.print(j+" ");
		}
	}

}
