package op;

public class AssignmentOperatorEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//복합 대입 연산자
		
		int result = 10;
		
		result+=10;
		System.out.println("result= "+result);
		
		result-=5;
		System.out.println("result= "+result);
		
		result*=3;
		System.out.println("result= "+result);
		
		result/=5;
		System.out.println("result= "+result);
		
		result%=3;
		System.out.println("result= "+result);

	}

}
